        Map<String, Integer> map1 = new HashMap<>();
        Map<String, Integer> map2 = new HashMap<>();

        for (String s: words1)
            if(map1.containsKey(s)) map1.put(s, map1.get(s)+1);
            else map1.put(s, 1);

        for (String s: words2)
            if(map2.containsKey(s)) map2.put(s, map2.get(s)+1);
            else map2.put(s, 1);

        int count = 0;
        for(String i : map2.keySet()){
            int frequency = map2.get(i);
            if(map1.containsKey(i) && map1.get(i) == frequency)
                count += frequency;
        }
        return count;
    }
}

