            int pb = find(b);
            if (pa != pb) {
                if (size[pb] < size[pa]) {
                    parent[pa] = pb;
                    size[pb] += size[pa];
                    maxSize = Math.max(maxSize, size[pb]);
                } else {
                    parent[pb] = pa;
                    size[pa] += size[pb];
                    maxSize = Math.max(maxSize, size[pa]);
                }
                groupCount--;
            }
        }

        public int[] getState() {
            return new int[]{groupCount, maxSize};
        }
    }
}

