        for(int i = 0, c = m-1; i < m; i++,c--){ // c: 4,3,2,1..
            for(int j = 0, r = 0; j < n; j++, r++){ // r:0,1,2,3..
               matrix[r][c] = box[i][j];
            }
        }
    }
}

