class Solution {
    public String toGoatLatin(String sentence) {
        String[] words=sentence.toLowerCase().split(" ",0);
        int i=1;
        StringBuilder ans = new StringBuilder();
        for(String word:words){

            char p=word.charAt(0);
            StringBuilder k=new StringBuilder();
            if(p=='a'||p=='i'||p=='o'||p=='e'||p=='u'){
                k.append(word).append("ma");
            }
            else{
                k.append(word, 1, word.length());
                k.append(p);
                k.append("ma");
            }
            for(int m=0;m<i;m++){
               k.append('a');
            }
            ans.append(k).append(" ");
            i++;
        }
        return ans.toString().trim();
    }
}

