            while (!stack.isEmpty() && temperatures[i] >= stack.peek()[0]) {
                stack.pop();
            }
            if (stack.isEmpty()) {
                result[i] = 0;
            } else {
                result[i] = stack.peek()[1] - i;
            }
            stack.push(new int[]{temperatures[i], i});
        }
        return result;
    }
}

