        for (int i = 0; i < heights.length; i++) {
            if (stack1.size() == 0) {
                left[i] = pseudoIndex;
            } else if (stack1.size() > 0 && stack1.peek().first < heights[i]) {
                left[i] = stack1.peek().second;
            } else if (stack1.size() > 0 && stack1.peek().first >= heights[i]) {
                while (stack1.size() > 0 && stack1.peek().first >= heights[i]) {
                    stack1.pop();
