class Solution {
    public char[][] rotateTheBox(char[][] box) {
        int m = box.length;
        int n = box[0].length;
        char[][] matrix = new char[n][m];   

        turnBox(box, matrix, m, n); // turn the box clockwise 90 degree

        turnGravity(matrix, n, m); // cause that falling effect, since matrix turned so n,m

