                            j=2
                                diff==0 return true
                        dp[4][2][1] = true    
						return true
				    return dp[4][1][1000-3] = true
                return dp[3][1][0] = true

			i: 2
			j: 1
			diff: 2
			return dp[2][1][2] = true
			return true

		i: 0
		j: 1
		diff: 4
		return dp[0][1][4] = true  
    return true
