class Solution {
    public double new21Game(int n, int k, int maxPts) {
        if (k == 0 || n >= k + maxPts)
            return 1.0;

        double[] dp = new double[n + 1]; 
        double windowSum = 1.0;
        double probability = 0.0;

        dp[0] = 1.0; 

        for (int i = 1; i <= n; i++) {
            dp[i] = windowSum / maxPts; 

            if (i < k)
                windowSum += dp[i]; 
            if (i >= k)
                probability += dp[i]; 
            if (i - maxPts >= 0)
                windowSum -= dp[i - maxPts]; 
        }

        return probability;
    }
}

