class Solution {
    public int uniquePathsIII(int[][] grid) {

        int row = 0;
        int col = 0;
        for(int i = 0; i < grid.length; i++) {
            for(int j = 0; j < grid[0].length; j++) {
                if(grid[i][j] == 1) {
                    row = i;
                    col = j;

                    break;
                }
            }
        }
        boolean[][] used = new boolean[grid.length][grid[0].length];
        return count(grid, used, row, col);
    }
    int count(int[][] grid, boolean[][] used, int row, int col) {
        if (!isValid(used, grid, row, col)) {
            return 0;
        }
        if (grid[row][col] == 2) {
            return 1;
        }

        used[row][col] = true;

        int paths = count(grid, used, row + 1, col) +
                count(grid, used, row - 1, col) +
                count(grid, used, row, col + 1) +
                count(grid, used, row, col - 1);

        used[row][col] = false;
        return paths;
    }

    boolean isValid(boolean[][] used, int[][] grid, int row, int col) {
        if (row < 0 || col < 0 || row >= used.length || col >= used[0].length || used[row][col] || grid[row][col] == -1) {
            return false;
        }
        return true;
    }
}

