            lower.push(tree);
        }

        Set<int[]> res = new HashSet<>(lower);
        res.addAll(upper);
        return res.toArray(new int[res.size()][]);
    }

    public int angle(int[] A , int[] B , int[] C){
        return (C[0] - A[0]) * (B[1] - A[1]) - (B[0] - A[0]) * (C[1] - A[1]);
    }
