class Solution {
    public int[] maxPoints(int[][] a, int[] q) {

        int n = a.length;
        int m = a[0].length;

        int l = q.length;
        int[][] p = new int[l][2];

        for(int i = 0 ; i < l ; i++) {

            p[i][0] = i;
            p[i][1] = q[i];
        }

        Arrays.sort(p, (o1,o2)-> {

            if(o1[1] == o2[1]) {

                Integer.compare(o1[0],o2[0]);
            }

            return Integer.compare(o1[1],o2[1]);
        });

        PriorityQueue<int[]> qu = new PriorityQueue<>((o1,o2)->{

            return Integer.compare(a[o1[0]][o1[1]],a[o2[0]][o2[1]]);
        });

        qu.add(new int[]{0,0});

        boolean[][] v = new boolean[n][m];
        int cnt = 0;
        int idx = 0;

        while(qu.size() > 0 && idx < l) {

            int[] f = qu.remove();
            int r = f[0] , c = f[1];

            if(v[r][c]) continue;

            v[r][c] = true;

            if(a[r][c] < p[idx][1]) {

                cnt++;
            }
            else{

                q[p[idx][0]] = cnt;
                idx++;
                qu.add(f);
                v[r][c] = false;
                continue;
