            int start=-1;
            for(int y=i-1; y>=0; y--){
                if(s.charAt(y)==' '){
                    start=y;
                    break;
                }
            }
            ans.append(s.substring(start+1, i+1) + " ");
            i=start;
        }
        ans.deleteCharAt(ans.length()-1);
        return ans.toString();
    }
}

