class DSU:
    def __init__(self, n):
        self.par = list(range(n))
        self.rank = [1] * n
        self.size = n

    def find(self, u):
        if u != self.par[u]:
            self.par[u] = self.find(self.par[u])
        return self.par[u]

    def union(self, u, v):
        uu, vv = self.find(u), self.find(v)
