class Solution {
    public String toGoatLatin(String sentence) {
        sentence = sentence.toLowerCase();
        String[] words = sentence.split(" ",0);
        int i = 1;
        StringBuilder ans = new StringBuilder();
        for(String word:words){
            char p1 = word.charAt(word.length()-1);
            char p = word.toLowerCase().charAt(0);
            StringBuilder k = new StringBuilder();
            if(p=='a'||p=='i'||p=='o'||p=='e'||p=='u'){
                k.append(word).append("ma");
            }
            else{
                k.append(word.substring(1)).append(p1).append("ma");
            }
            for(int m = 0; m < i; m++){
               k.append('a');
            }
            ans.append(k);
            if(i != words.length) ans.append(" ");
            i++;
        }
        return ans.toString();
    }
}

