class Solution {
    public int largestInteger(int num) {
        String numString = ""+ num;
        List<Integer> even = new ArrayList<>();
        List<Integer> odd = new ArrayList<>();
        for(Character c : numString.toCharArray()){
            int n = Character.getNumericValue(c);
            if(n%2==0){
                even.add(n);
            }else{
                odd.add(n);
            }
