        double total=0;
        for(int i=0;i<product.length;i++){
            total+=(double)(amount[i]*stock.get(product[i]));
        }
        itr++;
        if(itr==n){
            total*=(double)(((100-discount)/100d));
            itr=0;
        }
        return total;
    }
}

