    public int solve(int[] values, int i,int j)
    {
        if(i>=j) return 0;
        if(dp[i][j]!=-1) return dp[i][j];
        int min=Integer.MAX_VALUE;
        for(int k=i;k<j;k++)
        {
            int temp=solve(values,i,k)+solve(values,k+1,j)+
                        values[i-1]*values[k]*values[j];
            min=Math.min(min,temp);
        }

        return dp[i][j]=min;
    }
}

