          map.put(tokenId, currentTime + timeToLive);
        }
    }

    public int countUnexpiredTokens(int currentTime) {
        int count = 0;
        for(String key : map.keySet()){
          if(map.get(key) > currentTime){
            count++;
          }
        }
        return count;
    }
}

