class Solution {
    public int totalStrength(int[] strength) {
        int mod = 1000000007;
        int len = strength.length;
        long[] prefix = prefixSum(strength, len, mod);
        Deque<Integer> stack = new ArrayDeque<>();
        stack.push(-1);
        long ans = 0;
        for(int i = 0; i < len; i++) {
            while(stack.peek() != -1 && strength[i] <= strength[stack.peek()]) {
                int mid = stack.pop();
                int left = stack.peek() + 1;
                int right = i - 1;
                int n = mid - left + 1;
                int t = right - mid + 1;
                long val = ((1l * n * (prefix[right + 1] - prefix[left]) + mod) % mod) * strength[mid];
                ans += val;
                ans %= mod;
            }
            stack.push(i);
        } 
        int right = len - 1;
        while(stack.peek() != -1) {
            int mid = stack.pop();
            int left = stack.peek() + 1;
            int n = mid - left + 1;
            int t = right - mid + 1;
            long val = ((1l * n * (prefix[right + 1] - prefix[left]) + mod) % mod) * strength[mid];
            ans += val;
            ans %= mod;
        }
        return (int) ((ans + mod) % mod);
    }
    private long[] prefixSum(int[] strength, int len, int mod) {
        long[] prefix = new long[len + 1];
        for(int i = 0; i < len; i++) {
            prefix[i + 1] = (prefix[i] + strength[i]) % mod;
        }
        return prefix;
    }
}
