class Solution:
    def countAnagrams(self, s: str) -> int:

        l = s.split()
        ans = 1

        for i in l:
            d = {}
        # counting frequencies of word i in dictionary d
            for j in i:
                if(d.get(j)):
                    d[j] += 1
                else:
                    d[j] = 1  

            duplicates = 1
            for j in d.values():
                duplicates *= math.factorial(j)
            curr = math.factorial(len(i))//duplicates

            ans *= curr
            ans = ans%mod

        return ans

