from typing import List
from functools import lru_cache

class Solution:
    def ways(self, pizza: List[str], k: int) -> int:
        self.r, self.c = len(pizza), len(pizza[0])

        tot_apples = 0
        self.pfsum_row = [[0]*self.c for _ in range(self.r)]
        self.pfsum_col = [[0]*self.c for _ in range(self.r)]

        for i in range(self.r-1, -1, -1):
            for j in range(self.c-1, -1, -1):
                if pizza[i][j] == 'A':
                    tot_apples += 1
                if i < self.r - 1:
                    self.pfsum_row[i][j] += self.pfsum_row[i + 1][j]
                if j < self.c - 1:
                    self.pfsum_col[i][j] += self.pfsum_col[i][j + 1]
            self.pfsum_row[i][j] = pizza[i][j] == 'A' + (self.pfsum_row[i+1][j] if i+1 < self.r else 0)
            self.pfsum_col[i][j] = pizza[i][j] == 'A' + (self.pfsum_col[i][j+1] if j+1 < self.c else 0)

        if tot_apples < k:
            return 0

        if k == 1:
            return 1

        return self.getWays(0, 0, k - 1) % (10**9 + 7)


    @lru_cache(None)
    def getWays(self, i, j, k):
        remainder = self.pfsum_row[i][j] + self.pfsum_col[i][j] - (pizza[i][j]=="A")
        if remainder < k:
            return 0
        if k == 0:
            return 1
        ways = 0
        for row in range(i+1, self.r):
            if self.pfsum_row[row][j] - (pizza[row][j]=="A") < remainder:
                ways += self.getWays(row, j, k - 1)
        for col in range(j+1, self.c):
            if self.pfsum_col[i][col] - (pizza[i][col]=="A") < remainder:
                ways += self.getWays(i, col, k - 1)
        return ways

