# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def kthLargestLevelSum(self, root: TreeNode, k: int) -> int:
        dq = collections.deque([root])
        a = []
        while dq:
            lvlsum = 0
            for _ in range(len(dq)):
               n = dq.popleft()
               lvlsum += n.val
               if n.left: dq.append(n.right)
               if n.right: dq.append(n.left)
            a.append(lvlsum)
        if len(a) < k:
            return -1
        else:
            a.sort(reverse=True)
            return a[k-1]

