        graph = defaultdict(list)
        degree = defaultdict(int) # net out degree 
        for x, y in pairs: 
            graph[x].append(y)
            degree[x] += 1
            degree[y] -= 1

        for k in degree: 
            if degree[k] == 1: 
                x = k
                break 

