import collections

class Solution:
    def findSubstring(self, s: str, words: list[str]) -> list[int]:
        if not s or not words:
            return []

        word_map = collections.Counter(words)
        word_len = len(words[0])
        seq_len = word_len * len(words)

        result = []

        for i in range(len(s) - seq_len + 1):
            temp_map = dict(word_map)
            for j in range(i, i + seq_len, word_len):
                curr_word = s[j:j + word_len]
                if curr_word in temp_map:
                    temp_map[curr_word] -= 1
                    if temp_map[curr_word] < 1:
                        del temp_map[curr_word]
            if not temp_map:
                result.append(i)

        return result

