class Solution:
    def numMovStonesII(self,stones) -> List[int] :
        stones.sort()
        stone_length = len(stones)
        move_penultimate = stones[-1] - stones[1] - stone_length+2
        move_final = stones[-2] - stones[0] - stone_length+2  
        most_moves = max(move_penultimate, move_final)

        if move_penultimate == move_final == stone_length - 2:
            return [most_moves, most_moves]

        starting_index = 0
        max_legal_moves = 0 

        for stone in stones:
            while stones[starting_index] < stone - stone_length + 1:
                starting_index += 1
            max_legal_moves = max(max_legal_moves, stone_length - starting_index)

        return [stone_length- max_legal_moves, most_moves]

