           bool flag = false;
           for(int j=i;j<n;j++){
               if(ans[j]=='1' && lcp[i][j]!=0){
                   if(t>=26)
                   return "";
                   ans[j] ='a'+t;
                   flag = true;
               }
           }
           if(flag ==true)
           t++;
