        smallAns1=helper(n-1,dp)%mod;
        smallAns2=helper(n-4,dp)%mod;

        dp[n]=(smallAns1*2+smallAns2)%mod;
        return dp[n]%mod;
    }

    int numTilings(int n) {
        vector<long> dp(n+1,-1);
        long ans=helper(n,dp)%mod;
        return (int)ans;        
    }
};

