    std::unordered_map<int,int> x, y;

    void add(int number) { 
        ++x[number];
        if (x[number] != 1) 
            --y[x[number] - 1];
        ++y[x[number]];
    }

    void deleteOne(int number) { 
        if (x[number] != 0) {
            --x[number];
            if (y[x[number] + 1] != 0) 
                --y[x[number] + 1];
            ++y[x[number]];
        }
    }

    bool hasFrequency(int frequency) { 
        if (y[frequency] != 0) 
            return true;
        return false;
    }
};

