public:
 queue<int> q1, q2;
    MyStack() {

    }

    void add(int x) {  
        q1.push(x);
        while(!q1.empty()) {
            q2.push(q1.front());
            q1.pop();
        }
        swap(q1, q2);
    }

    int pop() {
        if(q1.empty()) {
            return -1; // or throw an exception
        }
        int a= q1.front();
        q1.pop();
        return a;
    }

    int top() {
        if(q1.empty()) {
            return -1; // or throw an exception
        }
        return q1.front();
    }

    bool empty() {
        return q1.empty();
