class Solution {
public:
    int countElements(std::list<int>& nums) {

        if(nums.size() <= 2) return 0;
        int ma = *std::max_element(nums.begin(),nums.end());
        int countma = std::count(nums.begin(),nums.end(),ma);
        int mi = *std::min_element(nums.begin(),nums.end());
        int countmi = std::count(nums.begin(),nums.end(),mi);
        int ans = nums.size() - (countma+countmi);
        if(ans <= 0) return 0;
        return ans;

    }
};

