class LRUCache {
public:
    class node
    {
        public:
        int key;
        int val;
        node* next;
        node* prev;
        node(int _key,int _val)
        {
            key = _key;
            val = _val;
            next = NULL;
            prev = NULL;
        }
    };

    node* head = new node(-1,-1);
    node* tail = new node(-1,-1);
    int capacity;
    unordered_map<int,node*> mp;

    void addNode(node* newNode) {
        node* temp = head->next;
        newNode->next = temp;
        newNode->prev = head;
        head->next = newNode;
        temp->prev = newNode;
    }

    void deleteNode(node* delNode) {
        node* delprev = delNode->prev;
        node* delnext = delNode->next;
        delprev->next = delnext;
        delnext->prev = delprev;
    }

    LRUCache(int capacity) {
        this->capacity = capacity;
        head->next = tail;
        tail->prev = head;
    }

    int get(int key) {
        if(mp.find(key) != mp.end()) {
            node* node = mp[key];
            int value = node->val;
            mp.erase(key);
            deleteNode(node);
            addNode(node);
            mp[key] = head->next;
            return value;
        }

        return -1;
    }

    void put(int key, int value) {
        if(mp.find(key) == mp.end()) {
            node* node = new node(key,value);
            if(mp.size() == capacity) {
                mp.erase(tail->prev->key);
                deleteNode(tail->prev);
            }

            addNode(node);
            mp[key] = head->next;
        }
        else {
            node* node = mp[key];
            mp.erase(key);
            deleteNode(node);
            addNode(node);
            mp[key] = head->next;
            node->val = value;
        }
    }
};

