 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
    // bs ye dhyan rkho leaf node se backtrack kro and return kro iss node ko kitne coins ki 
    // jarurat hai.. if -2 means 2 coins ki jarurat hai and 0 means no coins need
    // 2 means 2 coins extra hai...iss node + iske neeche saare nodes ke bharne ke baad
    // to kisi nodes ko kitne coins chahiye :- root->value ,-1 for this node coin
    // +l for left side se jo ans aaya if -ve ans aaya to subtract wrna add nd same for right
    // so, curr-1+l+r iss node ki requirement hai and iska parent itna coins isko lautaega if
    // this is -ve or ye itna coin parent ko lauta rha hai if it is +ve so 
    // in final cnt ... add its abs(...) since dono case mein coin parent to child ya child to parent move karega
    private:
    int cnt=0;
    int func(TreeNode *root) {
        if(!root) return 0;

        int l = func(root->left);
        int r = func(root->right);

        int temp = root->val -1 +l+r;
        cnt += abs(temp);

        return temp;
    }
public:
    int distributeCoins(TreeNode* root) {
        func(root);
        return cnt;
    }
};

