class AuthenticationManager {

    int timeToLive;
    HashMap<String, Integer> hashMap;
    TreeSet<Integer> treeSet;

    public AuthenticationManager(int timeToLive) {
        this.timeToLive = timeToLive;
        hashMap = new HashMap<>();
        treeSet = new TreeSet<>();
    }

    public void generate(String tokenId, int currentTime) {
        clear(currentTime);
        hashMap.put(tokenId, currentTime + timeToLive);
        treeSet.add(currentTime + timeToLive);
    }

    public void renew(String tokenId, int currentTime) {
        clear(currentTime);
        if(hashMap.containsKey(tokenId) && hashMap.get(tokenId) > currentTime) {
            hashMap.put(tokenId, currentTime + timeToLive);
            treeSet.add(currentTime + timeToLive);
        }
    }

    public int countUnexpiredTokens(int currentTime) {
        clear(currentTime);
        return treeSet.size();
    }

    private void clear(int currentTime) {
        while(!treeSet.isEmpty() && treeSet.first() <= currentTime) {
            int time = treeSet.pollFirst();
            hashMap.values().removeIf(val -> val != null && val == time);
        }
    }
}

